xpath.script:
document.addEventListener('DOMContentLoaded', function () {
// Select navigation links and content sections
const navLinks = document.querySelectorAll('.nav-link1');
const contentSections = document.querySelectorAll('.content-section');

// Add click event listener to each navigation link
navLinks.forEach(link => {
link.addEventListener('click', function (e) {
e.preventDefault(); // Prevent default anchor behavior

// Remove 'active' class from all nav links
navLinks.forEach(nav => nav.classList.remove('active'));

// Add 'active' class to the clicked link
this.classList.add('active');

// Hide all content sections
contentSections.forEach(section => section.classList.remove('active'));

// Get the ID of the section to show from the data attribute
const sectionId = this.getAttribute('data-section') + '-section';

// Show the selected section by adding the 'active' class
document.getElementById(sectionId).classList.add('active');
});
});
});

document.addEventListener("DOMContentLoaded", function () {
const cards = document.querySelectorAll(".card");

// Apply animation delay dynamically based on the index
cards.forEach((card, index) => {
card.style.animationDelay = `${0.1 * (index + 1)}s`; // Delay increases by 0.1s per card
});

// Observer options
const observerOptions = {
root: null, // Viewport as the root
threshold: 0.2, // 20% of the card must be visible
};

// Callback function for IntersectionObserver
const observerCallback = (entries, observer) => {
entries.forEach(entry => {
if (entry.isIntersecting) {
entry.target.classList.add("visible"); // Add the 'visible' class
observer.unobserve(entry.target); // Stop observing this card
}
});
};

// Create an IntersectionObserver
const observer = new IntersectionObserver(observerCallback, observerOptions);

// Observe each card
cards.forEach(card => observer.observe(card));
});

